/*
    The purpose of this file is to provide functionality of the resource page.
    It allows the user to choose a category and the resource page will display the
    relevant resources relevant to the chosen category.
*/
var category_links=document.getElementsByClassName("category-link"),resource_cards=document.getElementsByClassName("resource-card")
$(category_links[0]).click(function(){for(var e=0;e<category_links.length;e++)$(category_links[e]).removeClass(" is-active")
$(this).addClass(" is-active")
for(var r=0;r<resource_cards.length;r++)$(resource_cards[r]).show()})
for(var i=1;i<category_links.length;i++)$(category_links[i]).click(function(){for(var e=0;e<category_links.length;e++)$(category_links[e]).removeClass(" is-active")
$(this).addClass(" is-active")
for(var r=0;r<resource_cards.length;r++)for(var s=resource_cards[r].querySelectorAll(".tag"),a=0;a<s.length;a++){var c=$(s[a]).html(),t=$(this).html()
if(c.indexOf(t)>=0){$(resource_cards[r]).show()
break}$(resource_cards[r]).hide()}})
